import React, { useState, useEffect } from "react";
import Drinks from "./images/04.jpg";

export const Main = () => {
  const [cocktails, setCocktails] = useState([]);
  const [openImageIndex, setOpenImageIndex] = useState(null);
  const [isPopupOpen, setIsPopupOpen] = useState(false);

  useEffect(() => {
    const urls = [
      "https://www.thecocktaildb.com/api/json/v1/1/search.php?s=margarita",
      "https://www.thecocktaildb.com/api/json/v1/1/search.php?s=negroni",
    ];

    Promise.all(
      urls.map((url) => fetch(url).then((response) => response.json()))
    )
      .then((dataArray) => {
        const allCocktails = dataArray.reduce((acc, data) => {
          return acc.concat(data.drinks || []);
        }, []);

        setCocktails(allCocktails);
      })
      .catch((error) => {
        console.error("Error al consultar las API: ", error);
      });
  }, []);

  const openImage = (index) => {
    setOpenImageIndex(index);
    setIsPopupOpen(true);
  };

  const closeImage = () => {
    setOpenImageIndex(null);
    setIsPopupOpen(false);
  };

  const renderCocktailDetails = () => {
    if (openImageIndex !== null) {
      const cocktail = cocktails[openImageIndex];
      return (
        <div className="box2" onClick={closeImage}>
          <div className="box4">
            <img
              className="box3"
              style={{ maxWidth: "300px", maxHeight: "300px" }}
              src={cocktail.strDrinkThumb}
              alt={cocktail.strDrink}
            />

            <div className="cocktail-info">
              <p className="cocktail-name">{cocktail.strDrink}</p>
              <p className="cocktail-ingredients">
                Ingredients:{" "}
                {[...Array(15)].map((_, i) => cocktail[`strIngredient${i + 1}`])
                  .filter((ingredient) => ingredient)
                  .join(", ")}
              </p>
              <p className="cocktail-instructions">
                Instructions: {cocktail.strInstructions}
              </p>
            </div>
          </div>
        </div>
      );
    }
  };

  return (
    <div className="">
      <div className="stories">
        <div className="center">
          <img onClick={() => openImage(0)} src={Drinks} className="new" alt="" />
          {isPopupOpen && renderCocktailDetails()}
          <div className="post">Thanks for visit!</div>
        </div>
      </div>

      <div className="seccion">
        {/* Tus iconos SVG aqu√≠ */}
      </div>
      <main>
        {cocktails.map((cocktail, index) => (
          <div className="box" key={index}>
            <div onClick={() => openImage(index)}>
              <img
                className="box"
                src={cocktail.strDrinkThumb}
                alt={cocktail.strDrink}
              />
            </div>
          </div>
        ))}
      </main>
    </div>
  );
};
