import React, { useState, useEffect } from "react";

export const Main = () => {
  const [cocktails, setCocktails] = useState([]);

  useEffect(() => {
    fetch("https://www.thecocktaildb.com/api/json/v1/1/search.php?s=whiskey")
    fetch("https://www.thecocktaildb.com/api/json/v1/1/search.php?s=margarita")
    fetch("https://www.thecocktaildb.com/api/json/v1/1/search.php?s=whiskey")
      .then((response) => response.json())
      .then((data) => {
        // Accede a la lista de c贸cteles en la respuesta
        const cocktailsData = data.drinks || [];
        // Asigna la lista de c贸cteles al estado
        setCocktails(cocktailsData);
      })
      .catch((error) => {
        console.error("Error al consultar la API: ", error);
      });
  }, []);

  return (
    <main>
      {cocktails.map((cocktail, index) => (
        <div className="box" key={index}>
          <img
            className="box"
            src={cocktail.strDrinkThumb}
            alt={cocktail.strDrink}
          />
          {/* <p className="cocktail-name">{cocktail.strDrink}</p> */}
        </div>
      ))}
    </main>
  );
};
