import React, { useState, useEffect } from "react";
import Drinks from "./images/04.jpg";

export const Main = () => {
  const [cocktails, setCocktails] = useState([]);
  const [openImageName, setOpenImageName] = useState(null);

  useEffect(() => {
    // Define las URLs de las API que deseas consultar
    const urls = [
      "https://www.thecocktaildb.com/api/json/v1/1/search.php?s=margarita",
      "https://www.thecocktaildb.com/api/json/v1/1/search.php?s=negroni",
    ];

    // Utiliza Promise.all() para realizar las solicitudes de API en paralelo
    Promise.all(
      urls.map((url) => fetch(url).then((response) => response.json()))
    )
      .then((dataArray) => {
        // Combina los resultados de todas las solicitudes en una sola matriz
        const allCocktails = dataArray.reduce((acc, data) => {
          return acc.concat(data.drinks || []);
        }, []);

        // Agrega la imagen "Drinks" manualmente a la matriz de cocktails
        allCocktails.unshift({ strDrink: "Drinks", strDrinkThumb: Drinks });

        // Asigna la matriz combinada al estado
        setCocktails(allCocktails);
      })
      .catch((error) => {
        console.error("Error al consultar las API: ", error);
      });
  }, []);

  const openImage = (name) => {
    setOpenImageName(name);
  };

  const closeImage = () => {
    setOpenImageName(null);
  };

  return (
    <div className="">
      <div className="stories">
        <div className="center">
          <img
            onClick={() => openImage("Drinks")}
            src={Drinks}
            className="new"
            alt="Drinks"
          />
          <div className="post">Thanks for visit!</div>
        </div>
      </div>

      <div className="seccion">
        {/* Código de los íconos */}
      </div>
      <main>
        {openImageName !== null && (
          <div
            className="box2"
            style={{
              position: "fixed",
              top: 0,
              left: 0,
              height: "100vh",
              width: "100vw",
              background: "red",
              display: "grid",
              justifyContent: "center",
              alignItems: "center",
              zIndex: 9999,
              opacity: 1,
            }}
            onClick={closeImage}
          >
            <div className="box4">
              <img
                className="box3"
                style={{ maxWidth: "300px", maxHeight: "300px" }}
                src={Drinks}
                alt="Drinks"
              />

              {/* Resto del código para mostrar información de la imagen */}
            </div>
          </div>
        )}
        {cocktails.map((cocktail, index) => (
          <div className="box" key={index}>
            <div onClick={() => openImage(cocktail.strDrink)}>
              <img
                className="box"
                src={cocktail.strDrinkThumb}
                alt={cocktail.strDrink}
              />
            </div>
          </div>
        ))}
      </main>
    </div>
  );
};
